import React, { Component } from 'react';
import './App.css';

import Movie from './Movie'


const movies = [{
    id: 1,
    title: 'Star Wars'

  }, {
    id: 2,
    title: 'Spiderman',
    desc: "Webby"
  }, {
    id: 3,
    title: 'Punisher'
  }, {
    id: 4,
    title: 'Babbadook',
    desc: "Scary"
  }
];


class App extends Component {

  state = {
    movies: []
  }

  async componentDidMount() {
    try {
      const res = await fetch('https://api.themoviedb.org/3/discover/movie?api_key=bc2d60ca3b9f779ae5a2899b3d896069&language=en-US&sort_by=popularity.desc&include_adult=false&include_video=false&page=1')
      const movies = await res.json();
      console.log(movies)
      this.setState({
        movies: movies.results
      })
    }catch(e) {
      console.log(e)
      }

  }

  render(){

  return (
    <div className="App">
      <header className="App-header">
      <h1>MovieDB</h1>
      </header>
      {this.state.movies.map((movie) => 
          <Movie key={movie.id} movie={movie} />
      )}
    </div>
  );
}
}


export default App;
